INSERT INTO ArticleTab (ArtName, Price, CategoryID, Description, Quantity, ManufacturerID, Liked, Offer, Discount) VALUES

							('Lian Li PC-Q08B Wuerfel ohne Netzteil schwarz',93.31,1,'Das schwarz geb&uuml;rstete Aluminium Geh&auml;use besticht durch seine Schlichtheit im Design. F&uuml;r einen besonderen optischen Anreiz sorgt der blau leuchtende 140mm L&uuml;fter in der Vorderseite. Ein weiterer 120mm L&uuml;fter befindet sich im Deckel. Das Geh&auml;use bietet ausreichend Platz f&uuml;r ein externes 5,25&quot; (13,34cm) Laufwerk, wahlweise DVD oder BluRay, und gleich bis zu sechs interne 3,5&quot; (8,89cm) Festplatten. Die Montage der Festplatten erfolgt in zwei getrennten K&auml;figen. Zwei in der Front angebrachte USB 3.0 Ports erm&ouml;glichen den Anschluss weiterer Hardware.',10,1,0,'true',7.00),
('Techsolo TC-85S Midi Tower 400 Watt schwarz/silber',29.61,1,'Das TC-85 ist ein Midi Tower mit besonderem Design. Es ist schlicht und zugleich edel. Dank des Designs und der kompakten Abmessungen f&uuml;gt es sich in jedes Ambiente und bietet dennoch ausreichend Platz f&uuml;r Ihre Komponenten. F&uuml;r ATX, Micro ATX und Mini ITX Mainboards geeignet.',10,2,0,'true',0.00),
('MS-Tech CA-011 Desktop 400 Watt schwarz/rot',36.63,1,'Das CA-011 ist ein Slimline Geh&auml;use in roter Klavierlackoptik f&uuml;r Micro ATX Mainboards mit integriertem 400 Watt Netzteil. Das Geh&auml;use verf&uuml;gt zudem &uuml;ber Front HD Audio und Front USB Anschl&uuml;sse. Durch die kompakten Abmessungen k&ouml;nnen nur Micro ATX Mainboards und Low-Profile Steckkarten verbaut werden.',10,3,23,'false',0.00),
('HKC 7802GDR Midi Tower 420 Watt schwarz',40.43,1,'Effiziente Geh&auml;usebel&uuml;ftung: Zur besseren Entl&uuml;ftung des Geh&auml;uses ist ein 120 mm L&uuml;fter an der Front installiert. Ein weiterer 80 mm L&uuml;fter in der Seitenwand und zwei 80mm L&uuml;fter an der R&uuml;ckseite k&ouml;nnen optional montiert werden. Front-Access: In der Front befinden sich Front Access Elemente in Form von einem eSATA, zwei USB 2.0 und Audio Anschl&uuml;ssen.',10,4,0,'false',20.00),
('CoolerMaster Centurion 534 Plus Midi Tower ohne Netzteil schwarz/silber',59.26,1,'F&uuml;r diesen Artikel ist noch keine Produktbeschreibung vorhanden.',10,5,0,'false',0.00),
('1024MB HIS Radeon HD 7750 iSilence Passiv PCIe 3.0 x16 (Retail)',94.72,2,'Basierend auf der preisgekr&ouml;nten GCN-Architektur sind GPUs der AMD Radeon HD 7700 Serie mit dem branchenweit ersten 28 nm-GPU-Design f&uuml;hrend. Vollgepackt mit intelligenten Leistungsmerkmalen wie z.B. PCI Express 3.0, der effizienten AMD ZeroCore Energietechnologie oder der AMD Eyefinity Techologie f&uuml;r Aufl&ouml;sungen von mehr als 5.760 x 1.080, sind dieses die weltweit fortschrittlichsten Grafikkarten. Effizienz. Flexibilit&auml;t. Leistung. Das ist die DNA der AMD Radeon HD 7750 - der weltweit fortschrittlichsten Grafikkarte. Die AMD Eyefinity Technologie ist da, mit kompletter Unterst&uuml;tzung f&uuml;r Stereo 3D, universeller Rahmenkompensation und brandneuen Anzeigekonfigurationen. Ebenso k&ouml;nnen Sie mit dem PCI Express 3.0-Standard und Multi-GPU-Konfigurationen mit AMD CrossFire Technologie die Konkurrenz mit der revolution&auml;ren AMD Radeon HD 7750 ganz leicht &uuml;bertrumpfen. Sorgen Sie f&uuml;r h&ouml;here Taktraten und schnelleres Gaming mit der AMD PowerTune Technologie der AMD Radeon HD 7750 Grafikkarte. Noch schnelleres Abrufen und Bearbeiten von Daten als jemals zuvor mit AMD Anwendungsbeschleunigung mit beispielloser 28 nm-GCN-Architektur. Der Markt ist noch nicht bereit f&uuml;r 4K-Video - Radeon schon. Dank voller Unterst&uuml;tzung f&uuml;r HDMI (mit 4K) und DisplayPort 1.2 HBR2 ist AMD Radeon HD 7750 bereit f&uuml;r Quad-HD.',10,6,343,'false',0.00),
('512MB Asus Radeon HD 5450 Silent Series Low Profile V2 Passiv PCIe 2.1 x16 (Retail)',26.73,2,'Die ATI Radeon&trade; HD 5450 Grafikkarte l&auml;sst im Bereich Gaming und Entertainment keine W&uuml;nsche offen. Erweitern Sie Ihr Sichtfeld mit der ATI Eyefinity Technologie. Beschleunigen Sie mit der ATI Stream Technologie anspruchsvolle Anwendungen, und erreichen Sie mit Ihrem Computer mehr als jemals zuvor.',10,7,0,'false',0.00),
('6144MB PowerColor Radeon HD 7990 Devil 13 Edition Aktiv PCIe 3.0 x16 (Retail)',902.33,2,'F&uuml;r diesen Artikel ist noch keine Produktbeschreibung vorhanden.',10,8,353,'false',40.00),
('4096MB Palit GeForce GTX 680 JetStream Aktiv PCIe 3.0 x16 (Retail)',526.09,2,'Der GeForce GTX 680 Prozessor ist ein schneller und fortschrittlicher Grafikprozessor, den NVIDIA je gebaut hat. Aber damit nicht genug. Die GPU Boost Technologie steigert dynamisch die Taktfrequenz f&uuml;r maximale Leistung. Adaptive V-Sync sorgt f&uuml;r ideale, ruckelfreie Frameraten und NVIDIA Surround erm&ouml;glicht Gaming auf drei Bildschirmen mit nur einer Grafikkarte. Bisher liefen Grafikprozessoren bei 3D Games, mit einer festen Taktfrequenz, selbst wenn sie das Potenzial f&uuml;r h&ouml;here Taktungen hatten. GPU Boost &uuml;berwacht st&auml;ndig die Arbeitslast des Grafikprozessors und steigert nach M&ouml;glichkeit die Taktfrequenz. Der Grafikprozessor l&auml;uft immer mit optimaler Leistung und liefert maximale Frameraten. Die leistungsstarke Palit GeForce GTX 680-Architektur bietet mehr als modernste Funktionen und Technologien. Sie bietet eine einmalige Leistung und wird die Art und Weise des Spielerlebnis sowohl aktueller als auch kommender Spitzentitel nachhaltig ver&auml;ndern!',10,9,6,'false',20.00),
('1024MB ZOTAC GeForce GTS 450 ECO Edition Aktiv PCIe 2.0 x16 (Retail)',67.2,2,'Die ZOTAC&reg; GeForce&reg; GTS 450 ECO Edition zeichnet sich durch eine herausragende Energieeffizienz mit einem bis zu 33 Prozent niedrigeren Energieverbrauch aus. Ausgestattet mit 192 Prozessorkernen und dem &uuml;ber 128-bit angebundenen 1024 MB gro&szlig;en DDR3 Speicher bietet die GTS 450 ECO dabei eine exzellente Leistungsf&auml;higkeit sowohl in den neusten DirectX&reg; 11 Spielen als auch in CUDA&trade; unterst&uuml;tzten Applikationen. Die ZOTAC&reg; GeForce&reg; GTS 450 ECO Edition im Single-Slot Design ist zudem die perfekte Grafikkarte f&uuml;r Heimkino-Liebhaber und &uuml;berzeugt mit starken Home-Theater-PC Features wie Hardware-Video-Beschleunigung zur Dekodierung von High-Definition-Videos, Dolby&reg; TrueHD und DTS-HD Master Audio&trade; Bitstreaming- sowie HDMI 1.4a Unterst&uuml;tzung.',10,10,0,'false',0.00),
('ASRock Q77M vPro Intel Q77 So.1155 Dual Channel DDR3 mATX Retail',90.01,3,'F&uuml;r diesen Artikel ist noch keine Produktbeschreibung vorhanden.',10,11,0,'false',5.00),
('Gigabyte GA-F2A55M-DS2 AMD A55 So.FM2 Dual Channel DDR3 mATX Retail',50.03,3,'F&uuml;r diesen Artikel ist noch keine Produktbeschreibung vorhanden.',10,12,7979,'true',0.00),
